Config.signature = 'LoyDgIk——欲买桂花同载酒，终不似，少年游';
//签名设置。



/*－－－－类型选择－－－－*/
Config.hitokototype = ''; //一言类型
/*类型详细:https://s1.ax1x.com/2020/05/03/JzYHcd.jpg*/

Config.musicClass = '热歌榜';
//分类[热歌榜|新歌榜|飙升榜|抖音榜|电音榜]

Config.bookmarkclass = ''; //书签分类
Config.historicalType =''; // 历史分类
Config.collectionType =''; //收藏分类

Config.weathercustomisation = '';
