List.lists_1 =[
      '影视森林@@hiker://home@影视/电影@@https://ftp.bmp.ovh/imgs/2021/01/7b3b311a0b446880.png',
 '腾讯@@hiker://home@腾讯@@https://img.rruu.net/image/5eb0bd7617e7d',
    '爱奇艺@@hiker://home@爱奇艺@@https://img.rruu.net/image/5eb11ccd88c21',
