let tem2 = fetch('hiker://files/HikerHome/arrayConfiguration.json', {});
try{
    eval(fetch('hiker://files/rules/js/homeUtility.js', {}));
} catch(e) {}
if (tem == '' || tem2 == '') {
    writeFile('hiker://files/HikerHome/arrayConfiguration.json', JSON.stringify(cardArr, null, 4));
    writeFile('hiker://files/HikerHome/homeConfiguration.json', JSON.stringify(Config, null, 4));
} else if(localConfig) {
    try {
        eval('Config =' + tem + ';let ar=' + tem2);
        for (let i in ar) {
            cardArr[i].openClose = ar[i].openClose;
        }
    }catch(e) {}
}
cardArr.sort((attribute => (a,b) =>
a[attribute] - b[attribute])('id'));
for (let i = 0; i < cardArr.length; i++) {
    if (!cardArr[i].openClose)
        continue;
    if (cardArr[i].title != undefined && cardArr[i].title !== '*') {
        d.push({
            title: cardArr[i].title,
            url: cardArr[i].url,
            col_type: cardArr[i].titleStyle
        })
    }
    let team = eval(cardArr[i].list);
    for (let j = 0; j < team.length; j++) {
        let k = team[j].split(/\s*@@\s*/);
        if (k.length < 2)
            continue;
        let tmp = false;
        if (!Config.localPattern) {
            for (let h = 0; h < cardArr[i].Styles.length; h++) {
                let flag = h ? cardArr[i].Styles[h - 1].numEnd: 0;
                if (j >= flag && j < cardArr[i].Styles[h].numEnd + flag)
                    tmp = cardArr[i].Styles[h].style;
            }
        } else {
            tmp = 'text_3';
        }
        if (!tmp)
            break;
        d.push({
            title: k[0],
            url: k[1],
            pic_url: k[2],
            col_type: tmp
        });
    }
}

//底部留白
/*d.push({
    col_type: 'long_text'
});
*/
//🌄首页图片======================
if (!Config.localPattern && (Config.tool || Config.homepage)) {
    try {
        let json, desc='', title = '', url = Config.userqriginalpictureurl == Config.wallpaperurl ? Config.randomPic ? Config.userqriginalpictureurl + '#.jpg' + timestamp: Config.userqriginalpictureurl + '#.jpg': Config.userqriginalpictureurl;
        let wrap = detailWrap ? '\n': '\t '
        switch (Config.tool) {
            case undefined:
            case 0:
                break;
            case 1:
                json = JSON.parse(fetch('https://v1.hitokoto.cn/?c=' + Config.hitokototype, {}));
                let source = json.from_who;
                if (source == null || source == 'null')
                source = '侠名';
                title = json.hitokoto;
                desc = '出处：\t《' + json.from + '》' + wrap + '作者：\t' + source;
                break;
            case 2:
                json = JSON.parse(fetch('https://api.uomg.com/api/rand.music?sort=' + Config.musicClass + '&format=json', {}));
                title = json.data.name;
                desc = '歌手:' + json.data.artistsname;
                url = json.data.url + '.mp3&memoryPosition=full';
                break;
            case 3:
                json = JSON.parse(fetch('https://v1.alapi.cn/api/weather/now?location=' + Config.weathercustomisation, {}));
                title = '当前位置:' + json.data.basic.location + '\t 更新于:' + json.data.update.loc;
                desc = '天气概况：温度:(' + json.data.now.tmp + '℃)\t\t体感:(' + json.data.now.fl + '℃)\t\t天气:('+json.data.now.cond_txt+')' + wrap + '风向:' + json.data.now.wind_dir + '\t风力:' + json.data.now.wind_sc + '级\t湿度:' + json.data.now.hum + '%\t降水:' + json.data.now.pcpn + 'mm';
                break;
            default:
                setError('我怀疑你在乱搞');
        }
        let toolbox = {};
        toolbox.title = title;
        toolbox.url = Config.homepage ? url: 'toast://'+title;
        toolbox.desc = desc;
        toolbox.pic_url = Config.randomPic ? Config.wallpaperurl + '#.jpg' + timestamp: Config.wallpaperurl + '#.jpg';
