    d.push({
        title: text,
        url: "hiker://bookmark",
        col_type: Config.functionStyle
    });
    let book = JSON.parse(fetch('hiker://bookmark', {}));
    let team = Config.bookmarkclass ? book.filter(item => item.group == Config.bookmarkclass): book;
    team.map(data => d.push({
        title: data.title,
        url: data.url.replace(/.*rule:\/\//g, 'rule://').replace(/.*hiker:\/\/home@/g, 'hiker://home@'),
        col_type: Config.bookmarkStyle
    }))
}

try{
    //历史记录
    if (Config.hikerHistory){
        var hData = [];
        let loc = typeof historyIndex == 'number'?historyIndex: historyIndex();
        hData.push({
            title: '🏷历史记录(‘‘点此查看更多’’)🏷',
            url: "hiker://history",
            col_type: Config.functionStyle
        });
        history(loc+1);
    }
    //我的收藏
    if (Config.hikerCollection){
        var cData = [];
        let loc = typeof collectionIndex == 'number'?collectionIndex: collectionIndex();
        cData.push({
            title: '🏷收藏列表(‘‘点此管理收藏’’)🏷',
            url: "hiker://collection",
            col_type: Config.functionStyle
        });
        collection(loc+1);
     }
}catch(e){
    setError('\n——历史记录或者我的收藏出错:'+e.message);
}
//底部留白
d.push({
