if (rule.firstHeader == "area") allurl = rule.area_url;
if (rule.firstHeader == "year") allurl = rule.year_url;
/*主页*/
if (allurl == "主页") {
    var wallpaperurl = '';
    if (userwallpaperurl != '' && wallpapervalue != 0)
        wallpaperurl = userwallpaperurl;
    else
        switch (wallpapervalue) {
            case 0:
                break;
            case 1:
                var bing = fetch('https://cn.bing.com/HPImageArchive.aspx?format=js&idx=0&n=1&mkt=zh-CN', {})
                if (bing != null && bing.length > 0) {
                    eval('json=' + bing);
                    wallpaperurl = 'https://cn.bing.com/' + json.images[0].url;
                } else
                    wallpaperurl = 'https://api.xygeng.cn/bing/1366.php';
                break;
            case 2:
                wallpaperurl = 'http://api.btstu.cn/sjbz/?lx=suiji';
            case 3:
                wallpaperurl = 'https://uploadbeta.com/api/pictures/random';
            case 4:
                wallpaperurl = 'https://img.xjh.me/random_img.php';
            case 5:
                wallpaperurl = 'http://www.dmoe.cc/random.php';
            case 6:
                wallpaperurl = 'https://acg.yanwz.cn/api.php';
        }
    var hitokototitle = '';
    var hitokotodesc = '';
    var hitokotourl = '';
    switch (hitokotovalue) {
        case 0:
            break;
        case 1:
            var hitokoto = fetch('https://v1.hitokoto.cn/?c=' + hitokototype, {})
            if (hitokoto != null && hitokoto.length > 0) {
                eval('json=' + hitokoto);
                hitokototitle = json.hitokoto;
                hitokotodesc = '出处：\t《' + json.from + '》\n原作者：\t' + json.from_who;
                hitokotourl = 'https://hitokoto.cn/?id=' + json.id;
            }
            break;
    }
    if (wallpaperurl == '' && hitokotovalue != 0) {
        d.push({
            title: hitokototitle,
            col_type: 'text_1',
            desc: hitokotodesc,
            url: hitokotourl
        });
    }
    if (wallpaperurl != '') {
        if (istimestamp == 0)
            d.push({
                title: hitokototitle,
                col_type: 'pic_1',
                pic_url: wallpaperurl,
                desc: hitokotodesc,
                url: wallpaperurl
            });
        if (istimestamp == 1)
            d.push({
                title: hitokototitle,
                col_type: 'pic_1',
                pic_url: wallpaperurl + '#' + new Date().getTime(),
                desc: hitokotodesc,
                url: wallpaperurl
            });
    }
    if (isfilehomecontent == false) {
