var jsonZr = JSON.parse(fetch('hiker://home'));
var res = {};
var d = [];
var n = 0;
for (var i = 0; i <jsonZr.length; i++) {
    if ((jsonZr[i].title + jsonZr[i].author + JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) + JSON.stringify(jsonZr[i].url).match(/.*?hiker.*?/)).includes(getUrl().split('#')[1]))
    n++;
}
d.push({
    title: '您的本地共有' + jsonZr.length + '条规则',
    content: "共搜索到" + n + "条结果",
});
for (var i = 0; i <jsonZr.length; i++) {
    var r = {};
    r.title = jsonZr[i].title;
    var ruleAuthor = jsonZr[i].author;
    var ruleGroup = jsonZr[i].group;
    var ruleUrl = JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//);
    r.desc = "规则版本：" + jsonZr[i].version;
    if (jsonZr[i].author == null || jsonZr[i].author == undefined || jsonZr[i].author == '') {
       ruleAuthor = "未知";
} 
    if(JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) == null || JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) == undefined || JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) == '') {
        ruleUrl = "无法识别";
}
    if (JSON.stringify(jsonZr[i].url).includes("hiker")) {
       ruleUrl = jsonZr[i].url;
}
    if (jsonZr[i].group == null || jsonZr[i].group == undefined || jsonZr[i].group == '') {
       ruleGroup = "暂无分组";
}
    r.content = "规则作者：" + ruleAuthor + "\n所在分组：" + ruleGroup + "\n站点网址：" + ruleUrl;
    r.url = 'hiker://home@' + jsonZr[i].title;
    r.img = 'https://acg.xydwz.cn/gqapi/gqapi.php?t=' + new Date().getTime() / i * (i + 1);
    if ((jsonZr[i].title + jsonZr[i].author + JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) + JSON.stringify(jsonZr[i].url).match(/.*?hiker.*?/)).includes(getUrl().split('#')[1]))
    d.push(r);
}
res.data = d;
setSearchResult(res);

