        + '  '
        + parseDomForHtml(metaItems[1], 'Text')
        + '  '
        + '<font color="' + nodeColor + '">' + metaNode + '</font>';
    let metaTags = parseDomForArray('<div>' + metaItems[3] + '</div>', 'div&&.tag');
