        metaTag = (metaTag === '' ? metaTag : (metaTag + '  '))
            + parseDomForHtml(metaTags[i], 'Text')
    }
    metaTag = metaTag.replace('置顶', '““置顶””');
    meta = meta + '  ' + metaTag;
    let desc = '““””' + meta;
    if (showLike) {
        let likeItems = parseDomForArray(list[j], '.like&&.like-item');
        // setError(JSON.stringify(list[j]));
        let likeCount = parseDomForHtml(likeItems[0], 'Text');
        let commentCount = parseDomForHtml(likeItems[1], 'Text');
        let seeCount = parseDomForHtml(likeItems[2], 'Text');
        let like = '获赞：' + (likeCount != null && likeCount !== 'undefined' && likeCount !== ''  ? likeCount : '0')
            + '\t\t'
            + '评论：' + (commentCount != null  && commentCount !== 'undefined' && commentCount !== '' ? commentCount : '0')
            + '\t\t'
            +'查看：' + (seeCount != null  && seeCount !== 'undefined' && seeCount !== '' ? seeCount : '0');
        // 👍🏻👍❤️🧡♥️💛🖤♡♥💬👁️➿𓁺
