js:var res = {};var d = [];

let json = JSON.parse(getResCode());
let html = '<body>' + json.html + '</body>';

let list=parseDomForArray(html, 'body&&li');

for(let i in list){

d.push({
title:parseDomForHtml(list[i],'.subject-title&&Text'),
desc:parseDomForHtml(list[i],'.rating&&Text'),
pic_url:parseDomForHtml(list[i],'img&&src'),
url:parseDomForHtml(list[i],'a&&href').replace('/movie','https://movie.douban.com')+'#'+parseDomForHtml(list[i],'.subject-title&&Text') + '#noHistory#',
col_type:'movie_3'
});

}
res.data = d;
setSearchResult(res);

