    let ruleGroup = jsonZr[i].group;
    let ruleUrl = JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//);
    r.desc = "规则版本：" + jsonZr[i].version;
    if (jsonZr[i].author == null || jsonZr[i].author == undefined || jsonZr[i].author == '') {
        ruleAuthor = "未知";
    }
    if (JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) == null || JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) == undefined || JSON.stringify(jsonZr[i].url).match(/https?:\/\/.*?\//) == '') {
        ruleUrl = "无法识别";
    }
    if (JSON.stringify(jsonZr[i].url).includes("hiker")) {
        ruleUrl = jsonZr[i].url;
    }
    if (jsonZr[i].group == null || jsonZr[i].group == undefined || jsonZr[i].group == '') {
        ruleGroup = "暂无分组";
    }
    r.content = "规则作者：" + ruleAuthor + "\n所在分组：" + ruleGroup + "\n站点网址：" + ruleUrl;
