js:
eval(getVar("HIKERUTILS_RES"));
hikerUtil.load("home");
function addCodeOfUrlLabel(url, text) {
    return `<code><a href="hiker://empty@lazyRule=.js:putVar2('` + MY_URL.replace(/%%page%%.*%%page%%/g, "") + `','` + url + `');refreshPage();'toast://切换到` + text + `'">` + text + `</a></code>      `;
}
function addStrongLabel(text) {
    return `<strong>` + text + `</strong>`;
}
let domain = getVar("DIGA_DOMAIN");
let url = "";
if (getVar(MY_URL.replace(/%%page%%.*%%page%%/g, ""))) url = getVar(MY_URL.replace(/%%page%%.*%%page%%/g, "")).replace("fypage", MY_URL.match(/%%page%%(.*)%%page%%/)[1]);
else url = MY_URL.replace("hiker://empty/#/", base64Decode("aHR0cHM6Ly93d3cuenh6ai5tZS8=")).replace(/%%page%%/g, "");
let homePage = hikerUtil.request({ url: url });
let homeList = parseDomForArray(homePage, ".stui-vodlist&&li");
let items = homeList.map(data => ({
    title: parseDomForHtml(data, "h4&&Text"),
    url: domain + "/#/diga/" + parseDomForHtml(data, "h4&&a&&href").match(/detail\/(.*)\.html/)[1],
    pic_url: parseDomForHtml(data, "a&&data-original") + "@Referer=" + "https://" + parseDomForHtml(data, "a&&data-original").split("//")[1],
    desc: parseDomForHtml(data, ".pic-text&&Text")
}));
let hdUlList = parseDomForArray(homePage, "#screenbox&&ul");
let hdTitle = "";
hdUlList.forEach((u, uI) => {
    let hdLiList = parseDomForArray(u, "ul&&li");
    hdLiList.forEach((l, lI) => {
        if (lI == 0) hdTitle += addStrongLabel(parseDomForHtml(l, "span&&Text"));
        else if (l.indexOf("active") != -1) hdTitle += `<font color="#ff9900"> ` + parseDomForHtml(l, "a&&Text") + ` </font>`;
        else {
            let urlArr = parseDomForHtml(l, "a&&href").split("-");
            urlArr[8] = "fypage";
            let url = urlArr.join("-");
            hdTitle += addCodeOfUrlLabel(base64Decode("aHR0cHM6Ly93d3cuenh6ai5tZQ==") + url, parseDomForHtml(l, "a&&Text"));
        }
    });
    if (uI + 1 < hdUlList.length) hdTitle += "<br/>";
});
if (MY_URL.split("%%page%%")[1] == 1)
    items.unshift({
        title: hdTitle,
        col_type: "rich_text"
    });
setHomeResult({ data: items });

