searchFind:
js:var res = {};var d = [];

let json = JSON.parse(getResCode());
let html = '<body>' + json.html + '</body>';

let list=parseDomForArray(html, 'body&&li');

for(let i in list){

d.push({
title:parseDomForHtml(list[i],'.subject-title&&Text'),
desc:parseDomForHtml(list[i],'.rating&&Text'),
pic_url:parseDomForHtml(list[i],'img&&src'),
url:parseDomForHtml(list[i],'a&&href').replace('/movie','https://movie.douban.com')+'#'+parseDomForHtml(list[i],'.subject-title&&Text') + '#noHistory#',
col_type:'movie_3'
});

}
res.data = d;
setSearchResult(res);

detail_find_rule:
js:
let jsFile = getVar("db_js_file");
jsFile = jsFile === '' ? "hiker://files/rules/js/rt-douban.js" : jsFile;
eval(fetch(jsFile,{}));
douban.detailView();

preRule:
let settings = {
    /**
     * 是否开启视界搜，开启后点击第一项即可使用
     *
     * 若不开启则点击为查看原文
     *
     * 开启/1 不开启/0
     */
    openHikerSearch: 1,
    // desc 描述的三行中每行的最大长度
    descLineMaxLen: 25,
    // 评论用户颜色
    userColor: "#2979FF",
    // 模块的间距
    line_model: 1,
    // 模块间距类型（适配以后可能会有的无边距线）
    line_type_model: 'line_blank',

    // 评分显示类型，0 为星星★☆，1 为文字
    ratingType: 0,
    // 是否显示'[查看原文]', 0 为不显示，1 为显示
    showSource: 0,
    // 是否显示'[查看全文]', 0 为不显示，1 为显示
    showAllContent: 0,
    // 评分对应文字、星星、颜色 [ 五星力荐，四星推荐，三星还行，二星较差，一星很差]
    ratingTexts: [
        "力荐",
        "推荐",
        "还行",
        "较差",
        "很差",
    ],
    ratingStars: [
        "★★★★★",
        "★★★★☆",
        "★★★☆☆",
        "★★☆☆☆",
        "★☆☆☆☆",
    ],
    ratingColors: [
        "#00d437",
        "#9cdc00",
        "#ddcf00",
        "#ffa400",
        "#ff7800",
    ],

    /**
     * 搜索模式
     *
     * 0: "hiker://search?s=测试，将会打开搜索弹窗，自动填充搜索的关键词“测试”，由用户自己选择分组和规则后搜索",
     * 1: "hiker://search?s=测试&group=②影搜，将会打开搜索弹窗，自动填充搜索的关键词“测试”并且自动切换分组为“②影搜”，用户可切换分组和规则",
     * 2: "hiker://search?s=测试&rule=海阔视界，将会直接跳转搜索结果界面，自动选择标题为“海阔视界”的规则进行搜索，并且该界面不可切换别的规则",
     * 3: "hiker://search?s=测试&rule=海阔视界&simple=false，将会自动填充搜索的关键词“测试”，并且自动切换分组为“①推荐”、自动选中标题为“海阔视界”的规则，用户可切换分组和规则，并自动开始搜索"
     */
    searchMode: 0,
    // 搜索分组
    searchGroup: '',
    // 搜索规则
    searchRule: '',
    searchValue: '',
    jsFile: "hiker://files/rules/js/rt-douban.js",
    settingsCacheFile: 'hiker://files/douban/settings.json',
    openSimpleDetailView: 0
}

function writeObjectToFile(fileUrl, object) {
    writeFile(fileUrl, JSON.stringify(object))
}

function writeSettingsToFile(settings) {
    writeObjectToFile(settings.settingsCacheFile, settings);
}

try {
    let settingsTextInFile = fetch(settings.settingsCacheFile, {});
    settings = JSON.parse(settingsTextInFile);
} catch (e) {
    writeSettingsToFile(settings)
}

/**
 * 以下完全不需要使用者配置，如看不懂代码请勿乱动
 */
let searchValues = [
    '',
    settings.searchGroup === '' ? '' : '&group=' + settings.searchGroup,
    settings.searchRule === '' ? '' : '&rule=' + settings.searchRule,
    settings.searchRule === '' ? '' : '&rule=' + settings.searchRule + '&simple=false'
];
settings.searchValue = searchValues[settings.searchMode];
putVar2('db_js_file', settings.jsFile);
putVar2('db_settings', JSON.stringify(settings));

/* hiker-utils start */
