find_rule:
js: var json = JSON.parse(getResCode());
var allurl = JSON.parse(getRule());
var res = {};
if (allurl.class_url.indexOf('subject_collection') != -1 && allurl.firstHeader == 'class') {
    var list = json.subject_collection_items;
    var items = list.map(data => ({
        title: data.title,
        url: (data.type == 'movie' || data.type == 'tv' ? 'https://movie.douban.com/subject/' + data.id + '/#' + data.title : data.type == 'book' ? 'https://book.douban.com/subject/' + data.id + '#' + data.title: data.url)+';get;utf-8;{User-Agent@windows}',
        desc: '⭐️ ' + (data.rating == null ? '暂无' : data.rating.value),
        pic_url: data.cover.url
    }));
}
if ((allurl.area_url.indexOf('rank_list') != -1 && allurl.firstHeader == 'area') || (allurl.year_url.indexOf('year_ranks') != -1 && allurl.firstHeader == 'year')) {
    var items = [];
    var list = json.groups;
    for (var i = 0; i < list.length; i++) {
        try {
            var r = {};
            var j = list[i];
            if (j.title != "")
                r.title = '🏆 ' + j.title +' 🏆';
            r.col_type = 'text_center_1';
            if (j.title != "")
                items.push(r);
            var list2 = j.selected_collections;
            for (var i2 = 0; i2 < list2.length; i2++) {
                try {
                    var r2 = {};
                    var j2 = list2[i2];
                    r2.title = '\n\n🦋\r' + j2.title;
                    r2.pic_url = j2.cover_url;
                    r2.url = 'https://m.douban.com/' + j2.sharing_url.split('?uri=/')[1]+ '#' + r2.title +';get;utf-8;{User-Agent@windows}';
                    r2.col_type = 'movie_1';
                    items.push(r2);
                    var list3 = j2.items;
                    var items2 = list3.map(data => ({
                        title: data.title,
                        url: data.url + '#' + data.title +';get;utf-8;{User-Agent@windows}',
                        desc: '⭐️ ' + (data.rating == null ? '暂无' : data.rating.value),
                        pic_url: data.pic.normal,
                        col_type: 'movie_3'
                    }));
                    items.push.apply(items, items2);
                } catch (e) {}
            }
        } catch (e) {}
    }
}
res.data = items;
setHomeResult(res);

