/**
 * 是否开启视界搜，开启后点击第一项即可使用
 *
 * 若不开启则点击为查看原文
 *
 * 开启/1 不开启/0
 */
let openHikerSearch = 1;
// desc 描述的三行中每行的最大长度
let descLineMaxLen = 25;
// 评论用户颜色
let userColor = "#2979FF";
// 模块的间距
let line_model = 1;
// 模块间距类型（适配以后可能会有的无边距线）
let line_type_model = 'line_blank';
/**
 * 搜索模式
 *
 * 0: "hiker://search?s=测试，将会打开搜索弹窗，自动填充搜索的关键词“测试”，由用户自己选择分组和规则后搜索",
 * 1: "hiker://search?s=测试&group=②影搜，将会打开搜索弹窗，自动填充搜索的关键词“测试”并且自动切换分组为“②影搜”，用户可切换分组和规则",
 * 2: "hiker://search?s=测试&rule=海阔视界，将会直接跳转搜索结果界面，自动选择标题为“海阔视界”的规则进行搜索，并且该界面不可切换别的规则",
 * 3: "hiker://search?s=测试&rule=海阔视界&simple=false，将会自动填充搜索的关键词“测试”，并且自动切换分组为“①推荐”、自动选中标题为“海阔视界”的规则，用户可切换分组和规则，并自动开始搜索"
 */
let searchMode = 0;
// 搜索分组
let searchGroup = '';
// 搜索规则
let searchRule = '';

/**
 * 以下完全不需要使用者配置，如看不懂代码请勿乱动
 */
let searchValues = [
    '',
    searchGroup === '' ? '' : '&group=' + searchGroup,
    searchRule === '' ? '' : '&rule=' + searchRule,
    searchRule === '' ? '' : '&rule=' + searchRule + '&simple=false'
];
let searchValue = searchValues[searchMode];
putVar2('db_open_hiker_search', openHikerSearch.toString());
putVar2('db_desc_line_max_len', descLineMaxLen.toString());
putVar2('db_user_color', userColor);
putVar2('db_line_model', line_model.toString());
putVar2('db_line_type_model', line_type_model);
putVar2('db_search_value', searchValue);

