        var homeRulesKey = "5rW36ZiU6KeG55WM77yM6aaW6aG16aKR6YGT5ZCI6ZuG77+laG9tZV9ydWxlX3VybO+/pQ==";
        // setError (JSON.stringify(rules));
        writeObjectToFile(filename, rules);
        var str = base64Decode(homeRulesKey) + filename;
