var urlSplits = MY_URL.split('#');
var mUrl = urlSplits[0];
var page = parseInt(urlSplits[1]);
var maxPage = page;
try {
    maxPage =  parseInt (parseDomForHtml(html,"#j_data&&data-maxpage"));
} catch (e) { }

let imgError = '<p><font color="grey">图片暂时无法显示，若要查看请<a href="' + mUrl + '">访问原文</a></font></p>';
String.prototype.replaceWithCallback = function (searchValue, replaceValue, callback) {
    let returnValue = this;
    if (callback != null) {
        returnValue = callback(this, searchValue, replaceValue);
    }
    return returnValue;
};
function imgTag(context, imgUrl) {
    let replaceString = imgUrl === '' ? imgError : '<p><showimg src="' + (imgUrl + '@Referer=' + mUrl) + '"></p>';
    if (hideImg) {
        replaceString = imgUrl === '' ? imgError : '<p><a href="' + imgUrl + '">[点击查看大图]</a></p>';
    }
    return context.replace(/<img data-v>/g, replaceString);
}
function imgTagReplacement2(context, searchValue, replaceValue) {
    let matchArray = context.match(searchValue);
    let returnValue = context;
    if (matchArray != null) {
        for (let i = 0; i < matchArray.length; i++) {
            let item = matchArray[i];
            /<img.*?(data-original|data-src|src)="(.*?)"([\s\S]*?)>/.test(item);
            let imgUrl = RegExp.$2;
            if (imgUrl.indexOf("placeholder") > -1) {
                /<img.*?(data-original)="(.*?)"([\s\S]*?)>/.test(item);
                imgUrl = RegExp.$2;
            }
            if (imgUrl !== '') {
                returnValue = returnValue.replace(item, replaceValue);
                returnValue = imgTag(returnValue, imgUrl);
            }
        }
    }
    return returnValue;
}
let videoError = '<p><font color="grey">视频暂时无法显示，若要查看请<a href="' + mUrl + '">访问原文</a></font></p>';
function videoTag(context, videoUrl) {
    let replaceString = videoUrl === '' ? videoError : '<p><a href="' + (videoUrl) + '">[点击查看视频]</a></p>';
    if (hideImg) {
        replaceString = videoUrl === '' ? videoError : '<p><a href="' + videoUrl + '">[点击查看视频]</a></p>';
    }
    return context.replace(/<video data-v>/g, replaceString);
}
function videoTagReplacement2(context, searchValue, replaceValue) {
    let matchArray = context.match(searchValue);
    let returnValue = context;
    if (matchArray != null) {
        for (let i = 0; i < matchArray.length; i++) {
            let item = matchArray[i];
            /<video.*?(data-original|data-src|src)="(.*?)"([\s\S]*?)>/.test(item);
            let videoUrl = RegExp.$2;
            if (videoUrl !== '') {
                returnValue = returnValue.replace(item, replaceValue);
                returnValue = videoTag(returnValue, videoUrl);
            }
        }
    }
    return returnValue;
}
function getComment(nodeHtml, contentSearchVal) {
    // setError (nodeHtml);
    // let testContent = '';
    let replyContent = '';
    try {
        let authorHtml = parseDomForHtml(nodeHtml, ".author&&Html");
        let owner = '';
        try {
            owner = '<code>' + parseDomForHtml(authorHtml, ".post-owner&&Text") + '</code>';
        } catch (e) { }
        let author = '<br data-v><small>'
            + '<font color="' + userColor + '">' + parseDomForHtml(authorHtml, ".u&&Text") + owner + '</font>'
            + '\t<font color="grey">' + parseDomForHtml(authorHtml, ".stime&&Text") + '</font>'
            + '\t<font color="red">' + parseDomForHtml(authorHtml, ".ilike_icon_list&&Text")
            + '</font>';
        try {
            let floornum = '\t<font color="grey">' + parseDomForHtml(authorHtml, ".floornum&&Text") + '</font>';
            author = author + floornum;
        } catch (e) { }
        author = author + '</small>';
        let mContent = parseDomForHtml(nodeHtml, contentSearchVal);
        let mContentIndex = 0;
        let mContentSearchVal = /(<\/div>[\s\S][\r\n]*?[\s\S]<\/div>)/;
        mContentSearchVal = contentSearchVal === 'Html' ? /(<\/div>[\s\S][\r\n][\s\S].*?<\/div>)/ : mContentSearchVal;
        if(mContentSearchVal.test(mContent) === true) mContentIndex = mContent.indexOf(RegExp.$1);
        mContent = mContent.substring(mContentIndex)
            .replace(/<p>|<\/p>|(<a[\s\S].*?发自.*?客户端<\/a>)/g, "");
        // testContent = testContent == '' ? mContent : testContent;
        replyContent = mContent + author;
    } catch (e) { }
    replyContent = replyContent
        .replace(/:<br>/g, ':<br data-v>')
        .replace(/<div[\s\S].*?>|<(div|p|body|html|br)>|<\/(div|p|body|html)>/g, '')
        .replace (/<small class=".*?<\/small>/g, "")
        .replaceWithCallback(/<video([\s\S]*?)<\/video>/g, "<video data-v>", videoTagReplacement2);
    // setError (replyContent);
    return replyContent;
}

var content = '';
if(page > 1) {
    if (!(page > maxPage)) {
        try {
