});
    if (desc.length > 60) {
            desc = desc.replace(desc, desc.substring(0, 60) + "......");
      }

d.push({
          title: desc,
          col_type: 'long_text'
});

for (var i in conts) {
    var list = parseDomForArray(conts[i], 'ul&&li');
    if (list != null) {
        d.push({
            title: parseDomForHtml(tabs[i], 'li&&Text'),
            col_type: 'long_text'
        });
  for (var j in list) {
   d.push({
     title: parseDomForHtml(list[j],'a&&Text'),
     url: parseDom(list[j], 'a&&href') + `@lazyRule=.aplayer&&script&&Html.js:eval(input);player_data.url`,
       col_type: list.length >3?'text_3':'text_2'
            });
        }
    }
}
res.data = d;
setHomeResult(res);

