js:
var res = {};
var d = [];

//警告：本规则仅供写源爱好者学习交流。若作他用，后果自负！
//简介
var des_desc = parseDom(getResCode(), '.detail&&Text').replace(getUrl()+"/","");
//图片
var des_pic = parseDom(getResCode(), '.lazyload&&data-original');
//线路
var tabs = parseDomForArray(getResCode(), '.nav&&.play-ico');
//选集
var conts = parseDomForArray(getResCode(), '.tab-content&&ul');

d.push({
	title: '共有' + conts.length + '条线路',
 desc: des_desc,
	pic_url: des_pic,
	url: getUrl(),
	col_type: 'pic_1'
});

for (var i = 0; i < conts.length; i++) {
	var list = parseDomForArray(conts[i], 'ul&&li');
	if (list != null) {
		d.push({
			title: parseDomForHtml(tabs[i],'Text'),
			col_type: 'text_1'
		});
		for (var j = 0; j < list.length; j++) {
			d.push({
				title: parseDomForHtml(list[j],'Text'),
				url: 'http://youlebe.com'+list[j].split('href="')[1].split('"')[0]+'@lazyRule=html&&.stui-player__video&&script&&Html.js:eval(input)；；player_data.url'
			});
		}
	}
}
res.data = d;
setHomeResult(res);
