var urlSplits = MY_URL.split('#');
var mUrl = urlSplits[0];

if (mUrl.indexOf('subject_collection') > -1) {
    var res = {};
    var d = [];
    var colType = 'rich_text';
    var html = getResCode();

    let url = 'https://m.douban.com/rexxar/api/v2/'
        + mUrl.split('https://m.douban.com/')[1]
        + 'items?start=0&count=500&items_only=1&for_mobile=1';
    let json = JSON.parse(fetch(url, {
        headers: {
            'referer': mUrl
