    d = json.subject_collection_items.map(data => ({
        title: data.title,
        url: data.url + '#' + data.title + `@rule=js: eval(fetch(getVar("db_js_file"),{}));douban.detailView();`,
        desc: 'â­ï¸ ' + (data.rating == null ? 'æš‚æ— ' : data.rating.value),
        pic_url: data.pic ? data.pic.normal : data.cover.url,
        col_type: 'movie_3'
    }));
    res.data = d;
    setHomeResult(res);
} else {
    eval(fetch(getVar("db_js_file"),{}));
    douban.detailView();
}

