js:
var res = {};
var items = [];
var origin_url = getUrl();
var bianhao = origin_url.split('#')[1];
var json = JSON.parse('{"data":' + getResCode() + '}');
var j = json.data;
if (j.length > 0) {
    for (var i = 0; i < j.length; i++) {
        if (j[i].bianhao == bianhao) {
            items.push({
                title: j[i].bofangdizhi.mingcheng,
                col_type: 'text_1'
            });
            var jj = j[i].bofangdizhi.dizhizu;
            for (var ii = 0; ii < jj.length; ii++) {
                items.push({
                    title: jj[ii].biaoji,
                    url: jj[ii].dizhi
                });
            }
        }
    }
}
res.data = items;
setHomeResult(res);

