js: var json = JSON.parse(getResCode());
var allurl = JSON.parse(getRule());
var res = {};
if (allurl.class_url.indexOf('subject_collection') != -1 && allurl.firstHeader == 'class') {
    var list = json.subject_collection_items;
    var items = list.map(data => ({
        title: data.title,
        url: (data.type == 'movie' || data.type == 'tv' ? 'https://movie.douban.com/subject/' + data.id + '/#' + data.title : data.type == 'book' ? 'https://book.douban.com/subject/' + data.id + '#' + data.title: data.url)+';get;utf-8;{User-Agent@windows}',
        desc: 'â­ï¸ ' + (data.rating == null ? 'æš‚æ— ' : data.rating.value),
        pic_url: data.cover.url
    }));
}
if ((allurl.area_url.indexOf('rank_list') != -1 && allurl.firstHeader == 'area') || (allurl.year_url.indexOf('year_ranks') != -1 && allurl.firstHeader == 'year')) {
    var items = [];
    var list = json.groups;
    for (var i = 0; i < list.length; i++) {
        try {
            var r = {};
