var remoteUrl = "";

var apiType = "1";  // 0 ä¸ºæ–‡ä»¶ç›´é“¾ï¼Œ1 ä¸ºç äº‘API
var apiTypeJS = authorList.match(/apiType=.[\s\S]*?'/) + ";";
eval(apiTypeJS);
