    var access_token = null;  // ç”¨æˆ·æˆæƒç ï¼Œåœ¨ç äº‘"è®¾ç½®â€”>å®‰å…¨è®¾å®š->ä¸ªäººè®¿é—®ä»¤ç‰Œ"ä¸­å¯ä»¥ç”Ÿæˆ
    var tokenJS = authorList.match(/access_token=.[\s\S]*?'/) + ";";
    eval(tokenJS);
